mapper.select:
SELECT * FROM ( SELECT ROWNUM AS no, t.* FROM ( SELECT p.pdt_id AS pdtId, p.pdt_name AS pdtName, p.pdt_amount AS pdtAmount, p.pdt_discount_rate AS pdtDiscountRate, p.pdt_img_url AS pdtImgUrl, p.pdt_img_url2 AS pdtImgUrl2, p.pdt_review_count AS pdtReviewCount, (CASE WHEN p.pdt_discount_rate != 0 THEN p.pdt_amount - (p.pdt_amount * (p.pdt_discount_rate / 100.0)) ELSE p.pdt_amount END) AS pdtDiscountAmount, c.cat_name AS catName FROM O_PRODUCT p LEFT JOIN O_CATEGORY c ON p.cat_id = c.cat_id
p.cat_id = #{categoryNumber}
AND p.pdt_name LIKE '%' || #{keyword} || '%'
ORDER BY p.pdt_sales_count DESC
ORDER BY p.pdt_viewcount DESC
ORDER BY p.pdt_adddate DESC
) t ) b WHERE no BETWEEN #{start} AND #{end}

SELECT COUNT(*) FROM O_PRODUCT p
p.cat_id = #{categoryNumber}
AND p.pdt_name LIKE '%' || #{keyword} || '%'

mapper.update:
		UPDATE o_product 
        SET pdt_viewcount = pdt_viewcount + 1 
        WHERE pdt_id = #{productId}
